/*
Implements EIP20 token standard: https://github.com/ethereum/EIPs/issues/20
.*/
pragma solidity ^0.4.24;

import "./zeppelin/IERC20.sol";
import "./zeppelin/SafeMath.sol";
import "./bancor/IPowerLib.sol";

contract BondedToken is IERC20 {

    // EVENTS
    event Minted(uint256 amount, uint256 totalCost);
    event Burned(uint256 amount, uint256 reward);

    // LIBRARIES AND MATH CONSTANTS
    uint256 constant MAX_UINT256 = 2 ** 256 - 1;
    using SafeMath for uint;
    IPowerLib MathPowerLib;

    // TOKEN: normal related variables
    string public name;
    uint8 public decimals;
    string public symbol;
    mapping(address => uint256) balances;
    mapping(address => mapping(address => uint256)) allowed;
    uint256 public totalSupply;         // Integer to track the MAX amount of tokens to mint
    uint256 public currentSupply;       // Integer to track the amount of minted tokens

    // TOKEN: bonded curve related variables
    // Bancor Formula: c.x^(1/k - 1)
    // Custom references: c = multiplier / PRECISION  &&  1/k = shape / PRECISION
    uint32 public shape;                // Shape constant: "1/k" or Reserve Ratio inverse for Bancor Formula
    uint32 public multiplier;           // Multiplier constant: "c" or Supply for Bancor Formula
    uint32 public constant PRECISION = 1000000;
    IERC20 public reserveToken;
    uint256 public poolBalance;         // Integer to track total balance current supply for price calculations
    address registry;
    address voting;

    constructor(
        uint256 _maxTotalAmount,
        string _tokenName,
        uint8 _decimalUnits,
        string _tokenSymbol,
        address _registry,
        address _voting,
        IERC20 _reserveToken,
        uint32 _shape,
        uint32 _multiplier,
        IPowerLib _mathPowerLib
    ) public {
        currentSupply = 0;
        totalSupply = _maxTotalAmount;
        name = _tokenName;
        decimals = _decimalUnits;
        symbol = _tokenSymbol;

        // Set the corresponding Registry & Voting contract tied to this Token
        registry = _registry;
        voting = _voting;

        // The ERC20 token used to hold the stake value when minting new tokens
        reserveToken = _reserveToken;

        // Shape constant (equal to: '1/k' in Bancor Formula * PRECISION)
        shape = _shape;

        // Multiplier constant (equal to: 'c' in Bancor Formula * PRECISION)
        multiplier = _multiplier;

        // Math lib to efficiently calculate math power function
        MathPowerLib = _mathPowerLib;
    }

    /**
     * Total number of tokens that could be minted
     * @return The total amount of tokens that can be minted
     */
    function totalSupply() external view returns (uint256) {
        return totalSupply;
    }

    /**
     * Gets the balance of the specified address
     * @param _owner The address to query the balance of.
     * @return The amount owned by the passed address
     */
    function balanceOf(address _owner) view public returns (uint256) {
        return balances[_owner];
    }

    /**
     * Check the amount of tokens that an owner allowed to a spender
     * @param _owner address The address which owns the funds
     * @param _spender address The address which will spend the funds
     * @return The amount of tokens still available for the spender
     */
    function allowance(address _owner, address _spender) view public returns (uint256) {
        return allowed[_owner][_spender];
    }

    /**
     * Transfer token for a specified address
     * @param _to The address to transfer to.
     * @param _value The amount to be transferred.
     * @return The result of if the action has been successfully completed
     */
    function transfer(address _to, uint256 _value) public returns (bool) {
        require(balances[msg.sender] >= _value && balances[_to].add(_value) > balances[_to]);
        balances[msg.sender] = balances[msg.sender].sub(_value);
        balances[_to] = balances[_to].add(_value);
        emit Transfer(msg.sender, _to, _value);
        return true;
    }

    /**
     * Approve the passed address to spend the specified amount of tokens on behalf of msg.sender.
     * @dev Beware that changing an allowance with this method brings the risk that someone may use both the old
     * and the new allowance by unfortunate transaction ordering. One possible solution to mitigate this
     * race condition is to first reduce the spender's allowance to 0 and set the desired value afterwards:
     * https://github.com/ethereum/EIPs/issues/20#issuecomment-263524729
     * @param _spender The address which will spend the funds.
     * @param _value The amount of tokens to be spent.
     * @return The result of if the action has been successfully completed
     */
    function approve(address _spender, uint256 _value) public returns (bool) {
        allowed[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    /**
     * Transfer tokens from one address to another.
     * @param _from The address which you want to send tokens from
     * @param _to The address which you want to transfer to
     * @param _value The amount of tokens to be transferred
     * @return The result of if the action has been successfully completed
     */
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool) {
        require(balances[_from] >= _value);

        // Allow Registry to transfer tokens without approve needed to avoid extra tx
        if (msg.sender != registry && msg.sender != voting) {
            require(allowed[_from][msg.sender] >= _value);
        }

        balances[_to] = balances[_to].add(_value);
        balances[_from] = balances[_from].sub(_value);

        // Do not subtract allowed of Registry or Voting contracts
        if (msg.sender != registry && msg.sender != voting) {
            if (allowed[_from][msg.sender] < MAX_UINT256) {
                allowed[_from][msg.sender] = allowed[_from][msg.sender].sub(_value);
            }
        }

        emit Transfer(_from, _to, _value);
        return true;
    }

    /**
     * Mint new tokens with the reserve token at the the current rate
     * @dev The reserve token needed amount could be pre consulted using "priceToMint" function
     * @dev ~ 100000 gas
     * @param tokens The number of tokens you want to mint
     */
    function mint(uint256 tokens) external payable {
        require(tokens.add(currentSupply) <= totalSupply);
        uint256 priceForTokens = priceToMint(tokens);

        require(reserveToken.balanceOf(msg.sender) >= priceForTokens);
        require(reserveToken.allowance(msg.sender, this) >= priceForTokens);
        require(reserveToken.transferFrom(msg.sender, this, priceForTokens));

        currentSupply = currentSupply.add(tokens);
        balances[msg.sender] = balances[msg.sender].add(tokens);
        poolBalance = poolBalance.add(priceForTokens);

        emit Minted(tokens, priceForTokens);
    }

    /**
     * Burn tokens to receive ether at the the current rate
     * @dev The reserve token to receive could be pre consulted using "rewardForBurn" function.
     * @dev ~ 90000 gas
     * @param tokens The number of tokens you want to burn
     */
    function burn(uint256 tokens) external {
        require(balances[msg.sender] >= tokens);

        uint256 reserveToReturn = rewardForBurn(tokens);
        currentSupply = currentSupply.sub(tokens);
        balances[msg.sender] = balances[msg.sender].sub(tokens);
        poolBalance = poolBalance.sub(reserveToReturn);

        reserveToken.transfer(msg.sender, reserveToReturn);

        emit Burned(tokens, reserveToReturn);
    }

    /**
     * Set the voting contract address that has special rights in TransferFrom function to avoid extra Tx
     * @param _voting The address of the voting contract of set Register
     */
    function setVoting(address _voting) external {
        require(msg.sender == registry);
        require(_voting != 0 && voting == 0);
        voting = _voting;
    }

    /**
     * Calculate the amount of eth reserved for a number of tokens according of the curve set
     * @dev Calculate integral of c.x^(1/k - 1) ->  c.k.x^(1/k)
     * References: c = multiplier / PRECISION && 1/k = shape / PRECISION
     * Integral result: multiplier.x^(shape/PRECISION).1/shape
     * Result multiplied for 1 ether to convert to wei
     * @param tokens The amount of tokens to calculate the eth reserved
     * @return The reserved value for the amount of token received
     */
    function curveIntegral(uint256 tokens) view internal returns (uint256) {
        require(tokens >= 0);

        // Special case for last tokens to burn or 0 tokens to mint
        if (tokens == 0) {
            return tokens;
        }

        // Power function usage: Return (Value, Precision) equal to Result = Value / (2 ^ Precision)
        (uint256 exponential, uint8 exponentialPrecision) = MathPowerLib.power(tokens, 1, shape, PRECISION);

        // Reference: X >> Y is equivalent to X / 2**Y and X << Y is equivalent to X * 2**Y
        return uint256(multiplier).mul(exponential).mul(1 ether).div(shape) >> exponentialPrecision;
    }

    /**
     * Get the current eth amount to send for minting the desired tokens according of the curve set
     * @param tokens The amount of tokens you want to mint
     * @return The price value for the amount of token received
     */
    function priceToMint(uint256 tokens) view public returns (uint256) {
        require(tokens > 0 && tokens.add(currentSupply) <= totalSupply);
        return curveIntegral(currentSupply.add(tokens)).sub(poolBalance);
    }

    /**
     * The current ETH amount to receive for burning the desired tokens according of the curve set
     * @param tokens The amount of tokens you want to Burn
     * @return The reward value for the amount of tokens to be burn
     */
    function rewardForBurn(uint256 tokens) view public returns (uint256) {
        require(poolBalance > 0 && currentSupply > 0 && currentSupply >= tokens);
        return poolBalance.sub(curveIntegral(currentSupply.sub(tokens)));
    }


}

