pragma solidity ^0.4.24;

import "./Registry.sol";
import "./BondedToken.sol";
import "./zeppelin/Ownable.sol";

contract RegistryFactory is Ownable {

    event RegistryCreated(address creator, IERC20 token, PLCRVoting voting, Registry registry,
        string name, string description);
    event HashWhitelisted(bytes32 _hash);
    event HashWhitelistedRemoved(bytes32 _hash);

    // Registry factory libs and base registry
    Registry public baseRegistry;
    IPowerLib public powerLib;
    IERC20 public reserveToken;

    // Maps Hash whitelist (do not needed to normal registry workflow) to allow or not allow value
    mapping(bytes32 => bool) public whitelist;

    /// @dev constructor deploys a new proxyFactory.
    constructor(IPowerLib _powerLib, IERC20 _reserveToken, Registry _baseRegistry) public {
        powerLib = _powerLib;
        reserveToken = _reserveToken;
        baseRegistry = _baseRegistry;
    }

    /**
     * Create a new Registry (with the associated Bonded Token and Voting contracts)
     * @dev ~ 2850000 gas
     * @param _supply The total supply of the associated Token
     * @param _tokenName The name of the associated Token (usually the same name of the Registry)
     * @param _decimals The number of decimals (eg: 18) of the associated Token
     * @param _symbol The symbol (eg: DT-RBT) of the associated Token
     * @param _shape Constant to define the bonded curve of the associated Token
     * @param _multiplier Constant to define the bonded curve of the associated Token
     * @param _parameters Initial parameters of the TRC: [minDeposit, applyStageLength, commitStageLength,
     * revealStageLength, dispensationPct, pDispensationPct, voteQuorum, exitTimeDelay, exitPeriodLen]
     * @param _registryName Main name of the registry
     * @return The created Registry
     */
    function newRegistryWithBondedToken(
        uint _supply,
        string _tokenName,
        uint8 _decimals,
        string _symbol,
        uint32 _shape,
        uint32 _multiplier,
        uint[] _parameters,
        string _registryName,
        string _description
    ) public returns (Registry) {
        // Create new registry
        // Registry registry = new Registry(); // Spend too much GAS in deploy (maybe compilation bug)
        Registry registry = Registry(deployNewRegistry(baseRegistry));

        // Create new PLCRVoting
        PLCRVoting voting = new PLCRVoting();

        // Creates a new IERC20 (bonded token)
        BondedToken bondedToken = new BondedToken(_supply, _tokenName, _decimals, _symbol,
            registry, voting, reserveToken, _shape, _multiplier, powerLib);

        // Initialize new created PLCRVoting
        voting.init(bondedToken, registry);

        // Initialize new created Registry
        registry.init(bondedToken, voting, _parameters, _registryName);

        emit RegistryCreated(msg.sender, bondedToken, voting, registry, _registryName, _description);
        return registry;
    }

    /**
     * Mint new registry tokens with the corresponding reserve token balance
     * @dev The reserve token needed amount could be pre consulted using "priceToMint" function
     * @dev ~ 150000 gas
     * @param _registryToken The address of the Bonded Token of the registry
     * @param _amount The number of tokens you want to mint
     */
    function mintRegistryToken(address _registryToken, uint256 _amount) external {
        require(_amount > 0);
        require(_registryToken != address(0));

        BondedToken bondedToken = BondedToken(_registryToken);
        uint256 priceForTokens = bondedToken.priceToMint(_amount);

        // Get MANA from sender
        require(reserveToken.balanceOf(msg.sender) >= priceForTokens);
        require(reserveToken.allowance(msg.sender, this) >= priceForTokens);
        require(reserveToken.transferFrom(msg.sender, this, priceForTokens));

        // Mint new tokens
        reserveToken.approve(_registryToken, priceForTokens);
        bondedToken.mint(_amount);

        // Transfer new minted tokens to sender
        bondedToken.transfer(msg.sender, _amount);

        // Check the balance of sender to be (at least) equal or more than amount requested
        require(bondedToken.balanceOf(msg.sender) >= _amount);
    }

    /**
     * Add new hash to whitelist
     * @param _hash IPFS hash of the whitelisted asset
     */
    function whitelistHash(bytes32 _hash) onlyOwner external {
        require(!whitelist[_hash], "Hash must not be whitelisted.");
        whitelist[_hash] = true;
        emit HashWhitelisted(_hash);
    }

    /**
     * Remove -already added- hash from the whitelist
     * @param _hash IPFS hash of the whitelisted asset to be removed
     */
    function removeWhitelistedHash(bytes32 _hash) onlyOwner external {
        require(whitelist[_hash], "Hash must be whitelisted.");
        whitelist[_hash] = false;
        emit HashWhitelistedRemoved(_hash);
    }

    /**
     * Set the Reserve Token to be use in all future Registry creations
     * @param _newReserveToken Any ERC20 implementation
     */
    function setReserveToken(IERC20 _newReserveToken) onlyOwner external {
        reserveToken = _newReserveToken;
    }

    /**
     * Set the Power Library to be use in all future Registry creations
     * @param _newPowerLib Any IPowerLib implementation (only Math "power" function needed)
     */
    function setPowerLib(IPowerLib _newPowerLib) onlyOwner external {
        powerLib = _newPowerLib;
    }

    /**
     * Set the base Registry to be use in all future Registry creations
     * @param _newRegistry Any Registry implementation
     */
    function setBaseRegistry(Registry _newRegistry) onlyOwner external {
        baseRegistry = _newRegistry;
    }

    /**
     * Deploy a Copy of a contract supplied
     * @dev THIS COULD BE REPLACED WITH A "new Target()" BUT FOR SOME REASON CONSUMES A LOT OF MORE GAS IN DEPLOYMENT
     * @param _target The base contract to me copy
     * @return newContract address of the new deployed contract
     */
    function deployNewRegistry(address _target) private returns (address newContract){
        assembly {
        // Find empty storage location using "free memory pointer"
            let contractCode := mload(0x40)

        // Add target address, with a 11 bytes [i.e. 23 - (32 - 20)] offset to later accomodate first part of the bytecode
            mstore(add(contractCode, 0x0b), _target)

        // First part of the bytecode, shifted left by 9 bytes, overwrites left padding of target address
            mstore(sub(contractCode, 0x09), 0x000000000000000000603160008181600b9039f3600080808080368092803773)

        // Final part of bytecode, offset by 43 bytes
            mstore(add(contractCode, 0x2b), 0x5af43d828181803e808314602f57f35bfd000000000000000000000000000000)

        // total length 60 bytes
            newContract := create(0, contractCode, 60)

            if iszero(extcodesize(newContract)) {
                revert(0, 0)
            }
        }
    }
}

